import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

import Stack from "@mui/material/Stack";
import SubmitPost from "../Components/SubmitPost";
import PostCard from "../Components/PostCard";
import Button from "@mui/material/Button";

import background from "./fudzi100.jpg";
import axios from "../axios";

export default function UsrePage() {
  const { id } = useParams();
  const [data, setData] = useState();
  const [isLoading, setIsLoading] = useState(true);
  const userData = useSelector((state) => state.auth.data);

  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get(`/user?username=${id}`);
      setData(res.data);
      setIsLoading(false);
    };
    fetchUser();
  }, [id]);

  console.log(userData?._id);
  const [followed, setFollowed] = useState(
    data?.user.followers.includes(userData?._id)
  );
  const handleClick = async () => {
    try {
      await axios.put(`/user/${id}`);

      setFollowed(!followed);
    } catch (err) {
      console.warn(err);
    }
  };

  return (
    <>
      <div
        style={{
          backgroundImage: `url(${background})`,
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
          position: "absolute",
          width: "100%",
          height: "40%",
          top: 0,
          left: 0,
          zIndex: -10,
        }}
      >
        background
      </div>
      <Stack spacing={2}>
        <Button variant="contained" disableElevation onClick={handleClick}>
          follow
        </Button>
        <SubmitPost />
        {isLoading ? (
          <span>loading</span>
        ) : (
          data.posts.map((obj, id) => <PostCard key={id} obj={obj} />)
        )}
      </Stack>
    </>
  );
}
