import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

import Stack from "@mui/material/Stack";
import SubmitPost from "../Components/SubmitPost";
import PostCard from "../Components/PostCard";
import Button from "@mui/material/Button";

import { Paper, Avatar, Typography } from "@mui/material";
import avatar from "./avatar.png";

import background from "./fudzi100.jpg";

import axios from "../axios";
import UserCard from "./UserCard";

export default function UsrePage() {
  const { id } = useParams();
  const [data, setData] = useState();
  const [isLoading, setIsLoading] = useState(true);
  const userData = useSelector((state) => state.auth.data);

  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get(`/user-post/${id}`);
      setData(res.data);
      setIsLoading(false);
    };
    fetchUser();
  }, [id]);

  console.log(data);
  const [followed, setFollowed] = useState(
    data?.user.followers.includes(userData?._id)
  );
  const handleClick = async () => {
    try {
      await axios.put(`/user/${id}`);

      setFollowed(!followed);
    } catch (err) {
      console.warn(err);
    }
  };
  console.log(data);
  return (
    <>
      <div
        style={{
          backgroundImage: `url(${background})`,
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
          position: "absolute",
          width: "100%",
          height: "40%",
          top: 0,
          left: 0,
          zIndex: -10,
        }}
      >
        background
      </div>
      <Stack spacing={2}>
        {isLoading ? (
          <span>loading</span>
        ) : (
          <>
            <Paper elevation={3} sx={{ padding: "10px" }}>
              <Stack direction="row" spacing={2}>
                <Avatar
                  alt="Remy Sharp"
                  src={data.user.avatarUrl === "" ? avatar : data.avatarUrl}
                  sx={{ width: 56, height: 56 }}
                />
                <Stack spacing={0}>
                  <Typography variant="h5" color="text.secondary">
                    {data.user.fullName}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Тут должен быть статус пользователя
                  </Typography>
                </Stack>
                <Stack direction="row" spacing={2}>
                  <Button
                    size="large"
                    variant="contained"
                    disableElevation
                    onClick={handleClick}
                  >
                    Подписаться
                  </Button>
                  <Button size="large" variant="contained">
                    Сообщение
                  </Button>
                </Stack>
              </Stack>
            </Paper>
            <SubmitPost />
            {data.posts.map((obj, id) => (
              <PostCard key={id} obj={obj} />
            ))}
          </>
        )}
      </Stack>
    </>
  );
}
