import { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import { addPosts } from "../redux/slices/posts";
import axios from "../axios";

import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Stack from "@mui/material/Stack";
import Avatar from "@mui/material/Avatar";
import IconButton from "@mui/material/IconButton";
import Paper from "@mui/material/Paper";

export default function SubmitComment() {
  const dispatch = useDispatch();
  const [text, setText] = useState("");
  const [imageURL, setImageURL] = useState("");
  const [loading, setLoading] = useState(false);
  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log("submit");
      //await axios.post("/posts", );
    } catch (err) {
      console.warn(err);
      alert("Ошибка при загрузке файла");
    }
  };

  return (
    <Stack spacing={2} sx={{ width: "100%", padding: "10px" }}>
      <Stack direction="row" alignItems="center" spacing={2}>
        <Avatar
          alt="user-avatar"
          src="https://www.vladtime.ru/uploads/posts/2018-03/1522438548_evropeyskaya-koshka-dikiy-kot.jpg"
          sx={{ width: 42, height: 42 }}
        />
        <TextField
          label="Написать комментарий..."
          value={text}
          onChange={(e) => setText(e.target.value)}
          variant="standard"
          maxRows={10}
          multiline
          fullWidth
        />
      </Stack>
      <Stack
        direction="row"
        alignItems="center"
        justifyContent={"space-between"}
        spacing={2}
      >
        <Stack direction="row" alignItems="center" spacing={0}>
          <IconButton
            color="primary"
            aria-label="upload picture"
            component="label"
          >
            <input hidden accept="image/*" type="file" />
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="26"
              height="26"
              fill="currentColor"
              class="bi bi-image"
              viewBox="0 0 16 16"
            >
              <path d="M6.002 5.5a1.5 1.5 0 1 1-3 0 1.5 1.5 0 0 1 3 0z" />
              <path d="M2.002 1a2 2 0 0 0-2 2v10a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V3a2 2 0 0 0-2-2h-12zm12 1a1 1 0 0 1 1 1v6.5l-3.777-1.947a.5.5 0 0 0-.577.093l-3.71 3.71-2.66-1.772a.5.5 0 0 0-.63.062L1.002 12V3a1 1 0 0 1 1-1h12z" />
            </svg>
          </IconButton>
          <IconButton
            color="primary"
            aria-label="upload picture"
            component="label"
          >
            <input hidden accept="image/*" type="file" />
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="32"
              height="32"
              fill="currentColor"
              class="bi bi-camera-video"
              viewBox="0 0 16 16"
            >
              <path
                fill-rule="evenodd"
                d="M0 5a2 2 0 0 1 2-2h7.5a2 2 0 0 1 1.983 1.738l3.11-1.382A1 1 0 0 1 16 4.269v7.462a1 1 0 0 1-1.406.913l-3.111-1.382A2 2 0 0 1 9.5 13H2a2 2 0 0 1-2-2V5zm11.5 5.175 3.5 1.556V4.269l-3.5 1.556v4.35zM2 4a1 1 0 0 0-1 1v6a1 1 0 0 0 1 1h7.5a1 1 0 0 0 1-1V5a1 1 0 0 0-1-1H2z"
              />
            </svg>
          </IconButton>
        </Stack>
        <Button onClick={onSubmit} size="large" variant="contained">
          Опубликовать
        </Button>
      </Stack>
    </Stack>
  );
}
