import { useState, useEffect } from "react";
import { Link } from "react-router-dom";

import axios from "../axios";
import { useSelector } from "react-redux";

import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CardContent from "@mui/material/CardContent";
import CardActions from "@mui/material/CardActions";
import Avatar from "@mui/material/Avatar";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import Stack from "@mui/material/Stack";
import Collapse from "@mui/material/Collapse";
import SubmitComment from "./SubmitComment";
import Comment from "./Comment";

export default function PostCard({ obj }) {
  const userData = useSelector((state) => state.auth.data);
  const [data, setData] = useState(obj);
  const [comment, setComment] = useState("");
  const [open, setOpen] = useState(false);

  useEffect(() => {
    axios
      .get(`/comment/${obj._id}`)
      .then((res) => {
        setComment(res.data);
      })
      .catch((err) => {
        console.warn(err);
      });
  }, []);

  const handleClickLike = () => {
    axios
      .put(`/post/like/${obj._id}`)
      .then((res) => {
        setData(res.data);
      })
      .catch((err) => {
        console.warn(err);
      });
  };

  const handleClickRepost = () => {
    axios
      .post(`/post/repost/${obj._id}`)
      .then((res) => {
        setData(res.data);
      })
      .catch((err) => {
        console.warn(err);
      });
  };

  return (
    <Card sx={{ maxWidth: "100%" }}>
      <Link to={`/user/${obj.user._id}`}>
        <div style={{ displa: "flex" }}>
          <div>
            <Avatar
              sx={{ bgcolor: "red" }}
              aria-label="recipe"
              src="https://www.vladtime.ru/uploads/posts/2018-03/1522438548_evropeyskaya-koshka-dikiy-kot.jpg"
            />
            <Stack>
              <Typography variant="body2">{obj.user.fullName}</Typography>
              <Typography variant="caption" display="block" gutterBottom>
                {obj.createdAt}
              </Typography>
            </Stack>
          </div>
          <IconButton aria-label="settings">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="currentColor"
              class="bi bi-three-dots-vertical"
              viewBox="0 0 16 16"
            >
              <path d="M9.5 13a1.5 1.5 0 1 1-3 0 1.5 1.5 0 0 1 3 0zm0-5a1.5 1.5 0 1 1-3 0 1.5 1.5 0 0 1 3 0zm0-5a1.5 1.5 0 1 1-3 0 1.5 1.5 0 0 1 3 0z" />
            </svg>
          </IconButton>
        </div>
      </Link>
      <CardContent>
        <Typography variant="body2" color="text.secondary">
          {obj.text}
        </Typography>
      </CardContent>
      {obj.imageUrl && (
        <CardMedia
          component="img"
          height="394"
          image="https://www.vladtime.ru/uploads/posts/2018-03/1522438548_evropeyskaya-koshka-dikiy-kot.jpg"
          alt="Paella dish"
        />
      )}

      <CardActions
        disableSpacing
        sx={{ flexDirection: "column", alignItems: "flex-start" }}
      >
        <Stack direction="row" alignItems="center" spacing={1}>
          <Stack
            direction="row"
            alignItems="center"
            spacing={0.3}
            onClick={handleClickLike}
            sx={{ cursor: "pointer" }}
          >
            <IconButton aria-label="add to favorites">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="22"
                height="22"
                fill={
                  data.likes.includes(userData?._id) ? "red" : "currentColor"
                }
                class="bi bi-heart"
                viewBox="0 0 16 16"
              >
                <path d="m8 2.748-.717-.737C5.6.281 2.514.878 1.4 3.053c-.523 1.023-.641 2.5.314 4.385.92 1.815 2.834 3.989 6.286 6.357 3.452-2.368 5.365-4.542 6.286-6.357.955-1.886.838-3.362.314-4.385C13.486.878 10.4.28 8.717 2.01L8 2.748zM8 15C-7.333 4.868 3.279-3.04 7.824 1.143c.06.055.119.112.176.171a3.12 3.12 0 0 1 .176-.17C12.72-3.042 23.333 4.867 8 15z" />
              </svg>
            </IconButton>
            <Typography variant="caption" sx={{ fontSize: "18px" }}>
              {data.likes?.length}
            </Typography>
          </Stack>
          <Stack
            direction="row"
            alignItems="center"
            spacing={0.3}
            onClick={() => setOpen(!open)}
          >
            <IconButton aria-label="add to favorites">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="22"
                height="22"
                fill="currentColor"
                class="bi bi-chat"
                viewBox="0 0 16 16"
              >
                <path d="M2.678 11.894a1 1 0 0 1 .287.801 10.97 10.97 0 0 1-.398 2c1.395-.323 2.247-.697 2.634-.893a1 1 0 0 1 .71-.074A8.06 8.06 0 0 0 8 14c3.996 0 7-2.807 7-6 0-3.192-3.004-6-7-6S1 4.808 1 8c0 1.468.617 2.83 1.678 3.894zm-.493 3.905a21.682 21.682 0 0 1-.713.129c-.2.032-.352-.176-.273-.362a9.68 9.68 0 0 0 .244-.637l.003-.01c.248-.72.45-1.548.524-2.319C.743 11.37 0 9.76 0 8c0-3.866 3.582-7 8-7s8 3.134 8 7-3.582 7-8 7a9.06 9.06 0 0 1-2.347-.306c-.52.263-1.639.742-3.468 1.105z" />
              </svg>
            </IconButton>
            <Typography variant="caption" sx={{ fontSize: "18px" }}>
              {comment.length}
            </Typography>
          </Stack>
          <Stack
            direction="row"
            alignItems="center"
            spacing={0.3}
            onClick={handleClickRepost}
          >
            <IconButton aria-label="repost">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="22"
                height="22"
                fill={
                  data.reposts.includes(userData?._id)
                    ? "green"
                    : "currentColor"
                }
                class="bi bi-reply"
                viewBox="0 0 16 16"
              >
                <path d="M6.598 5.013a.144.144 0 0 1 .202.134V6.3a.5.5 0 0 0 .5.5c.667 0 2.013.005 3.3.822.984.624 1.99 1.76 2.595 3.876-1.02-.983-2.185-1.516-3.205-1.799a8.74 8.74 0 0 0-1.921-.306 7.404 7.404 0 0 0-.798.008h-.013l-.005.001h-.001L7.3 9.9l-.05-.498a.5.5 0 0 0-.45.498v1.153c0 .108-.11.176-.202.134L2.614 8.254a.503.503 0 0 0-.042-.028.147.147 0 0 1 0-.252.499.499 0 0 0 .042-.028l3.984-2.933zM7.8 10.386c.068 0 .143.003.223.006.434.02 1.034.086 1.7.271 1.326.368 2.896 1.202 3.94 3.08a.5.5 0 0 0 .933-.305c-.464-3.71-1.886-5.662-3.46-6.66-1.245-.79-2.527-.942-3.336-.971v-.66a1.144 1.144 0 0 0-1.767-.96l-3.994 2.94a1.147 1.147 0 0 0 0 1.946l3.994 2.94a1.144 1.144 0 0 0 1.767-.96v-.667z" />
              </svg>
            </IconButton>
            <Typography variant="caption" sx={{ fontSize: "18px" }}>
              {data.reposts.length}
            </Typography>
          </Stack>
        </Stack>
        <Collapse in={open} timeout="auto">
          <SubmitComment data={data} onClickSort={(id) => setComment(id)} />
          {comment === "" || comment.length === 0 ? (
            <span>loading</span>
          ) : (
            comment.map((obj, id) => <Comment key={id} obj={obj} />)
          )}
        </Collapse>
      </CardActions>
    </Card>
  );
}
