import React from "react";

export default function SubmitPost() {
  return (
    <Grid container spacing={1}>
      <Grid item xs={10}>
        <TextField
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          variant="outlined"
          maxRows={10}
          multiline
          fullWidth
        />
      </Grid>
      <Grid item xs={1}>
        <Button onClick={onSubmit} size="large" variant="contained">
          Опубликовать
        </Button>
      </Grid>
    </Grid>
  );
}
