import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchPosts } from "../redux/slices/posts";

import Stack from "@mui/material/Stack";
import SubmitPost from "../Components/SubmitPost";
import PostCard from "../Components/PostCard";

export default function NewsFeed() {
  const dispatch = useDispatch();

  const [post, setPost] = useState([]);

  useEffect(() => {
    dispatch(fetchPosts());
  }, []);

  setPost(useSelector((state) => state.posts));
  const isLoading = useSelector((state) => state.posts.status) === "loading";

  const onAddPost = (description) => {
    /*
    const newList = [description, ...items];
    setItems(newList);
    */
  };

  return (
    <Stack spacing={2}>
      <SubmitPost onAddPost={onAddPost} />
      {isLoading ? (
        <span>loading</span>
      ) : (
        post.map((obj, id) => <PostCard key={id} obj={obj} />)
      )}
    </Stack>
  );
}
