import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

import Grid from "@mui/material/Unstable_Grid2";
import MenuList from "../Components/MenuList";
import Stack from "@mui/material/Stack";
import UserCardMini from "../Components/UserCardMini";
import { Box } from "@mui/material";

import axios from "../axios";

export default function UsrePage() {
  const { id } = useParams();
  const [data, setData] = useState();
  const [isLoading, setIsLoading] = useState(true);
  const [dataComments, setCommentsData] = useState();
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get(`/user?username=${id}`);
      setData(res.data);
      setIsLoading(false);
    };
    fetchUser();
  }, []);

  console.log(data);
  return (
    <Stack spacing={2}>
      <SubmitPost onAddPost={onAddPost} />
      {isLoading ? (
        <span>loading</span>
      ) : (
        data.map((obj, id) => <PostCard key={id} obj={obj} />)
      )}
    </Stack>
  );
}
